import { Lock, Mail, Search } from "lucide-react";

import { Input } from "@/components/ui/input";

export default function InputIconExample() {
  return (
    <div className="flex flex-col space-y-4 p-2">
      <Input placeholder="Search..." Icon={Search} iconPlacement="left" />
      <Input
        variant="pill"
        placeholder="Enter email"
        Icon={Mail}
        iconPlacement="right"
      />
      <Input
        variant="underline"
        type="password"
        placeholder="Enter password"
        Icon={Lock}
        iconPlacement="left"
      />
    </div>
  );
}
